// OrdersComponent.js

import React, { useState, useEffect } from 'react';

const LiveOrdersComponent = () => {
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Define the userInfo JSON object
    const userInfo = {
      userId: 1,
      password: "userPassword", // Note: Be cautious with sensitive information
      groupNum: 100
    };

    // Make a POST request with userInfo as the payload
    fetch('/api/data/get-live-orders', {
      method: 'POST', // Specify the method as POST
      headers: {
        'Content-Type': 'application/json' // Specify the content type as JSON
      },
      body: JSON.stringify(userInfo) // Stringify the userInfo object to send as the request body
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json(); // Parse the JSON response body
    })
    .then(data => setOrders(data)) // Update state with the fetched data
    .catch(error => console.error('Error fetching closed trades:', error)); // Handle any errors
  }, []); // The empty dependency array means this effect runs once on mount


  if (isLoading) return <div>Loading orders...</div>;
  if (error) return <div>Error fetching orders: {error}</div>;

  return (
    <div>
      <h2>Orders</h2>
      <ul>
        {orders.map(order => (
          <li key={order.id}>
            User: {order.userID}, Group {order.userGroup}, Order ID: {order.orderManagerID}, Price {order.orderPX}, Quantity: {order.quantity}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LiveOrdersComponent;
